import React,{useEffect,useState} from "react";
import Pokedetails from "./components/Pokedetails.jsx"
import ReactDOM from "react-dom";
import axios from "axios";
import Pokedex from "./components/Pokedex.jsx";



function App (props){
  const[data,setData]=useState([])
  const [view,setView] = useState("pokedex");
console.log(data);
  const changeView=(option) =>{
    setView(option)
  }
useEffect(()=>{
  axios.get("http://localhost:5000/poke/poke").then(res=>setData(res.data))
},[])
 const renderView=(option)=> {
    
    if (option === "pokedex") {
      return <Pokedex data={data}/>;
    } else {
      return <Pokedetails changeView={changeView}/>;
    }
  }

 
    return (
      <div>
        <div className="nav">
          <span className="logo"
            onClick={() => this.changeView('pokedex')}>
            PokeMongoDB
          </span>
          <span className={view === 'pokedex'
            ? 'nav-selected'
            : 'nav-unselected'}
            onClick={() => this.changeView('pokedex')}>
            See all Pokemons
          </span>
        </div>

        <div className="main">
          {renderView(view)}
        </div>
      </div>
    )}
    export default App
